import React, { useEffect } from "react";
import { fabric } from "fabric";
import Tooltip from "@mui/material/Tooltip";

export default function EditorContent({
  canvasRef,
  fabricRef,
  setWidth,
  setHeight,
  setPointerCords,
  setZoom,
  handleToolClick
}) {
  const [isMobile, setIsMobile] = React.useState(false);
  const [isTablet, setIsTablet] = React.useState(false);
  const [isDesktop, setIsDesktop] = React.useState(false);

  useEffect(() => {
    const checkScreenSize = () => {
      const width = window.innerWidth;
      setIsMobile(width <= 768);
      setIsTablet(width > 768 && width <= 1024);
      setIsDesktop(width > 1024);
    };

    checkScreenSize();
    window.addEventListener("resize", checkScreenSize);
    return () => window.removeEventListener("resize", checkScreenSize);
  }, []);

  useEffect(() => {
    if (!canvasRef.current) return;

    const container = canvasRef.current.parentElement;
    const canvas = new fabric.Canvas(canvasRef.current, {
      backgroundColor: "white",
      selection: true,
      preserveObjectStacking: true
    });
    fabricRef.current = canvas;

    // ResizeObserver для адаптивного размера canvas
    const resizeCanvas = () => {
      if (!container) return;
      const { width, height } = container.getBoundingClientRect();
      canvas.setWidth(width);
      canvas.setHeight(height);
      setWidth(width);
      setHeight(height);
      canvas.renderAll();
    };
    const resizeObserver = new ResizeObserver(resizeCanvas);
    resizeObserver.observe(container);
    resizeCanvas();

    // ---- Zoom колесом ----
    const handleMouseWheel = (e) => {
      e.e.preventDefault();
      let zoom = canvas.getZoom();
      zoom *= 0.999 ** e.e.deltaY;
      zoom = Math.min(Math.max(zoom, 0.01), 20);
      const point = new fabric.Point(e.e.offsetX, e.e.offsetY);
      canvas.zoomToPoint(point, zoom);
      setZoom(zoom);
    };

    // ---- Pan мышью ----
    let isDragging = false;
    let lastPosX = 0;
    let lastPosY = 0;

    const handleMouseDown = (e) => {
      if (e.e.altKey || e.e.ctrlKey) {
        isDragging = true;
        canvas.selection = false;
        lastPosX = e.e.clientX;
        lastPosY = e.e.clientY;
        canvas.setCursor("grab");
      }
    };

    const handleMouseMove = (e) => {
      if (isDragging) {
        const vpt = canvas.viewportTransform;
        vpt[4] += e.e.clientX - lastPosX;
        vpt[5] += e.e.clientY - lastPosY;
        canvas.requestRenderAll();
        lastPosX = e.e.clientX;
        lastPosY = e.e.clientY;
        canvas.setCursor("grabbing");
      } else {
        const pointer = canvas.getPointer(e.e);
        setPointerCords({ x: Math.round(pointer.x), y: Math.round(pointer.y) });
      }
    };

    const handleMouseUp = () => {
      isDragging = false;
      canvas.selection = true;
      canvas.setCursor("default");
    };

    const handleClearCords = () => setPointerCords({ x: 0, y: 0 });

    // ---- Pinch-to-zoom для мобильных ----
    let lastTouchDistance = null;

    const getTouchDistance = (touches) => {
      const [t1, t2] = touches;
      return Math.sqrt(
        Math.pow(t2.clientX - t1.clientX, 2) +
          Math.pow(t2.clientY - t1.clientY, 2)
      );
    };

    const handleTouchStart = (e) => {
      if (e.e.touches.length === 2) {
        lastTouchDistance = getTouchDistance(e.e.touches);
      }
    };

    const handleTouchMove = (e) => {
      if (e.e.touches.length === 2 && lastTouchDistance) {
        e.e.preventDefault();
        const currentDistance = getTouchDistance(e.e.touches);
        let zoom = canvas.getZoom() * (currentDistance / lastTouchDistance);
        zoom = Math.min(Math.max(zoom, 0.01), 20);

        const centerX = (e.e.touches[0].clientX + e.e.touches[1].clientX) / 2;
        const centerY = (e.e.touches[0].clientY + e.e.touches[1].clientY) / 2;
        const point = new fabric.Point(centerX, centerY);

        canvas.zoomToPoint(point, zoom);
        setZoom(zoom);
        lastTouchDistance = currentDistance;
      }
    };

    const handleTouchEnd = () => {
      lastTouchDistance = null;
    };

    // Подписки на события
    canvas.on("mouse:wheel", handleMouseWheel);
    canvas.on("mouse:down", handleMouseDown);
    canvas.on("mouse:move", handleMouseMove);
    canvas.on("mouse:up", handleMouseUp);
    canvas.on("mouse:out", handleClearCords);
    canvas.on("touch:gesture", handleTouchStart);
    canvas.on("touch:drag", handleTouchMove);
    canvas.on("touch:orientation", handleTouchEnd);
    canvas.on("touch:end", handleTouchEnd);

    return () => {
      resizeObserver.disconnect();
      canvas.dispose();
    };
  }, [canvasRef, fabricRef, setWidth, setHeight, setPointerCords, setZoom]);

  return (
    <div className="editor-content">
      <ul id="tool-actions">
        {[
          ["arrange", "Перемещение"],
          ["selection", "Выделение"],
          ["crop", "Обрезание"],
          ["figures", "Фигура"],
          ["eraser", "Ластик"],
          ["cut", "Вырезать"],
          ["fill", "Заливка"],
          ["gradient", "Градиент"],
          ["zoom", "Лупа"],
          ["picker", "Пипетка"],
          ["text", "Текст"],
          ["pencil", "Карандаш"],
          ["hand", "Рука"],
          ["lasso", "Лассо"]
        ].map(([tool, title]) => (
          <li key={tool} className="tool-action" onClick={() => handleToolClick(tool)}>
            <Tooltip title={title} arrow>
              <img src={`/Images/EditorIcons/${tool}.png`} alt={tool} />
            </Tooltip>
          </li>
        ))}
        <li className="tool-action">
          <Tooltip title="Главный цвет" arrow>
            <input type="color" id="main-color" />
          </Tooltip>
        </li>
        <li className="tool-action">
          <Tooltip title="Вторичный цвет" arrow>
            <input type="color" id="secondary-color" defaultValue="#ffffff" />
          </Tooltip>
        </li>
      </ul>
      <div className="canvas-wrapper">
        <div className="canvas-container">
          <canvas ref={canvasRef} />
        </div>
      </div>
    </div>
  );
}
