import React, { useEffect, useRef, useState } from 'react';
import { fabric } from 'fabric';
import EditorContent from './CanvasFunctions/EditorContent';
import MenuBar from './EditorMenu/MenuBar';

export default function Editor() {
  const canvasRef = useRef(null);
  const fabricRef = useRef(null);
  const [width, setWidth] = useState(window.innerWidth * 0.8);
  const [height, setHeight] = useState(window.innerHeight * 0.8);
  const [pointerCords, setPointerCords] = useState({ x: 0, y: 0 });
  const [zoom, setZoom] = useState(1);
  const [selectedTool, setSelectedTool] = useState(null);
  const [isActionMenuOpen, setIsActionMenuOpen] = useState(false);

  // ресайз холста при изменении окна
  useEffect(() => {
    const handleResize = () => {
      const newWidth = window.innerWidth * 0.8;
      const newHeight = window.innerHeight * 0.8;
      setWidth(newWidth);
      setHeight(newHeight);
      if (fabricRef.current) {
        fabricRef.current.setWidth(newWidth);
        fabricRef.current.setHeight(newHeight);
        fabricRef.current.renderAll();
      }
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const handleToolClick = (toolName) => {
    setSelectedTool(toolName);
    setIsActionMenuOpen(true);
  };

  const closeActionMenu = () => {
    setIsActionMenuOpen(false);
    setSelectedTool(null);
  };

  return (
    <div className="paint-wrapper">
      <MenuBar
        x={pointerCords.x}
        y={pointerCords.y}
        width={width}
        height={height}
        selectedTool={selectedTool}
        isActionMenuOpen={isActionMenuOpen}
        closeActionMenu={closeActionMenu}
      />
      <div className="canvas-container">
        <EditorContent
          canvasRef={canvasRef}
          fabricRef={fabricRef}
          setWidth={setWidth}
          setHeight={setHeight}
          setPointerCords={setPointerCords}
          zoom={zoom}
          setZoom={setZoom}
          handleToolClick={handleToolClick}
        />
      </div>
    </div>
  );
}
