import React, { useEffect } from 'react';
import { fabric } from 'fabric';
import Tooltip from '@mui/material/Tooltip';

export default function EditorContent({
  canvasRef,
  fabricRef,
  addRectangle,
  addCircle,
  setPointerCords,
  zoom,
  setZoom,
  handleToolClick
}) {

  useEffect(() => {
    if (!canvasRef.current) return;

    // Создаём Fabric canvas
    const canvas = new fabric.Canvas(canvasRef.current, {
      backgroundColor: 'white',
      selection: true,
      preserveObjectStacking: true,
    });
    fabricRef.current = canvas;

    // Функция resize под родительский контейнер
    const resizeCanvas = () => {
      const container = canvasRef.current.parentNode; // canvas-container
      canvas.setWidth(container.clientWidth);
      canvas.setHeight(container.clientHeight);
      canvas.renderAll();
    };

    // Resize сразу
    resizeCanvas();

    // Resize при изменении окна
    window.addEventListener('resize', resizeCanvas);

    // Zoom с мыши
    const handleMouseWheel = (e) => {
      e.e.preventDefault();
      const delta = e.e.deltaY;
      let newZoom = canvas.getZoom() * 0.999 ** delta;
      newZoom = Math.min(Math.max(newZoom, 0.01), 20);

      const point = new fabric.Point(e.e.offsetX, e.e.offsetY);
      canvas.zoomToPoint(point, newZoom);
      setZoom(newZoom);
    };

    // Pan
    let isDragging = false;
    let lastPosX = 0;
    let lastPosY = 0;

    const handleMouseDown = (e) => {
      if (e.e.altKey || e.e.ctrlKey) {
        isDragging = true;
        canvas.selection = false;
        lastPosX = e.e.clientX;
        lastPosY = e.e.clientY;
        canvas.setCursor('grab');
      }
    };

    const handleMouseMove = (e) => {
      if (isDragging) {
        const vpt = canvas.viewportTransform;
        vpt[4] += e.e.clientX - lastPosX;
        vpt[5] += e.e.clientY - lastPosY;
        canvas.requestRenderAll();
        lastPosX = e.e.clientX;
        lastPosY = e.e.clientY;
        canvas.setCursor('grabbing');
      } else {
        const pointer = canvas.getPointer(e.e);
        setPointerCords({ x: Math.round(pointer.x), y: Math.round(pointer.y) });
      }
    };

    const handleMouseUp = () => {
      isDragging = false;
      canvas.selection = true;
      canvas.setCursor('default');
    };

    const handleClearCords = () => setPointerCords({ x: 0, y: 0 });

    // Touch events
    let isTouch = false;
    let lastTouchDistance = 0;

    const handleTouchStart = (e) => {
      if (e.e.touches.length === 2) {
        isTouch = true;
        const [t1, t2] = e.e.touches;
        lastTouchDistance = Math.hypot(t2.clientX - t1.clientX, t2.clientY - t1.clientY);
      }
    };

    const handleTouchMove = (e) => {
      if (isTouch && e.e.touches.length === 2) {
        e.e.preventDefault();
        const [t1, t2] = e.e.touches;
        const currentDistance = Math.hypot(t2.clientX - t1.clientX, t2.clientY - t1.clientY);
        let newZoom = canvas.getZoom() * (currentDistance / lastTouchDistance);
        newZoom = Math.min(Math.max(newZoom, 0.01), 20);

        const centerX = (t1.clientX + t2.clientX) / 2;
        const centerY = (t1.clientY + t2.clientY) / 2;
        canvas.zoomToPoint(new fabric.Point(centerX, centerY), newZoom);
        setZoom(newZoom);
        lastTouchDistance = currentDistance;
      }
    };

    const handleTouchEnd = () => { isTouch = false; };

    // Навешиваем события
    canvas.on('mouse:wheel', handleMouseWheel);
    canvas.on('mouse:down', handleMouseDown);
    canvas.on('mouse:move', handleMouseMove);
    canvas.on('mouse:up', handleMouseUp);
    canvas.on('mouse:out', handleClearCords);

    canvas.on('touch:gesture', handleTouchStart);
    canvas.on('touch:drag', handleTouchMove);
    canvas.on('touch:orientation', handleTouchEnd);

    return () => {
      window.removeEventListener('resize', resizeCanvas);
      canvas.dispose();
    };
  }, [canvasRef, fabricRef, setPointerCords, setZoom]);

  return (
    <div className="editor-content">
      <ul id='tool-actions'>
        {/* Пример инструментов */}
        <li className='tool-action' onClick={() => handleToolClick('arrange')}>
          <Tooltip title="Перемещение" arrow>
            <img src='/Images/EditorIcons/arrange.svg' alt='arrange'/>
          </Tooltip>
        </li>
        <li className='tool-action' onClick={() => handleToolClick('selection')}>
          <Tooltip title="Выделение" arrow>
            <img src='/Images/EditorIcons/selection.png' alt='selection'/>
          </Tooltip>
        </li>
        {/* Остальные инструменты ... */}
      </ul>
      <div className='canvas-wrapper'>
        <div className='canvas-container'>
          <canvas ref={canvasRef} />
        </div>
      </div>
    </div>
  );
}
