import React, { useState } from 'react';
import { Box, TextField, Button, Typography, Stack, Fade } from '@mui/material';
import Register from './Register';

export default function Auth() {
  const [showRegister, setShowRegister] = useState(false);

  if (showRegister) {
    return <Register onBack={() => setShowRegister(false)} />;
  }

  return (
    <Fade in={!showRegister}>
      <Box
        component="form"
        sx={{
          maxWidth: 400,
          width: '100%',
          p: 3,
          borderRadius: 3,
          boxShadow: 3,
          bgcolor: 'white',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
        }}
      >
        <Stack spacing={2} sx={{ width: '100%' }}>
          <TextField label="Электронный адрес" variant="outlined" fullWidth />
          <TextField label="Пароль" type="password" fullWidth />

          <Typography
            variant="body2"
            color="primary"
            sx={{ textAlign: 'right', cursor: 'pointer' }}
          >
            Забыли пароль?
          </Typography>

          <Button
            variant="contained"
            fullWidth
            sx={{
              borderRadius: '25px',
              py: 1.2,
              fontWeight: 'bold',
              bgcolor: 'red',
              '&:hover': { bgcolor: 'darkred', transform: 'scale(1.02)' },
              transition: '0.3s',
            }}
          >
            Войти
          </Button>

          <Typography textAlign="center" sx={{ fontSize: 14 }}>
            ИЛИ
          </Typography>

          <Typography
            variant="body2"
            sx={{ textAlign: 'center', cursor: 'pointer', color: 'primary.main' }}
            onClick={() => setShowRegister(true)}
          >
            Ещё не зарегистрированы?{' '}
            <Box component="span" sx={{ fontWeight: 'bold', color: 'black' }}>
              Зарегистрироваться
            </Box>
          </Typography>
        </Stack>
      </Box>
    </Fade>
  );
}
